INSERT INTO `notifications` VALUES (1,'TỔ CHỨC CUỘC THI THẮP SÁNG ĐÈN TUỆ','to-chuc-cuoc-thi-thap-sang-den-tue','<h1 class=\"tt-ss tt-md\" style=\"box-sizing: border-box; margin: 0px 0px 8px; font-size: 22px; font-family: Roboto, Helvetica, Arial, sans-serif; font-weight: 500; line-height: 1.2; color: #194d7b; padding: 0px; border: 0px; position: relative; text-transform: uppercase; text-align: center;\">TỔ CHỨC CUỘC THI THẮP S&Aacute;NG Đ&Egrave;N TUỆ</h1>\r\n<div class=\"content-detail-post\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-size: 12pt; color: #222222; font-family: Roboto, Helvetica, Arial, sans-serif;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\"><img style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto 8px; padding: 0px; max-width: 100%;\" src=\"http://chuahoangphap.com.vn/hinhphatsu/vanban_hinh/web_image/thi_kinhphapcu2019.jpg\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-size: 24px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">BAN TỔ CHỨC CH&Ugrave;A HOẰNG PH&Aacute;P</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-size: 24px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">HUYỆN H&Oacute;C M&Ocirc;N &ndash; TP HỒ CH&Iacute; MINH</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">H&oacute;c M&ocirc;n, ng&agrave;y 14 th&aacute;ng 07 năm 2018</span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-size: 24px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">KẾ HOẠCH</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-size: 24px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Tổ chức Cuộc thi Thắp S&aacute;ng Đ&egrave;n Tuệ</span></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Để tiếp nối v&agrave; truyền lửa cho ngọn đ&egrave;n ch&aacute;nh ph&aacute;p được s&aacute;ng m&atilde;i với thời gian, đem t&igrave;nh thương v&agrave; tuệ gi&aacute;c của Phật đến với nh&acirc;n loại, chư Tăng Ch&ugrave;a Hoằng Ph&aacute;p quyết định tổ chức cuộc thi&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">&ldquo;Thắp&nbsp;S&aacute;ng Đ&egrave;n Tuệ&rdquo;</span>. Cụ thể như sau:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">I. MỤC Đ&Iacute;CH V&Agrave; &Yacute; NGHĨA</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- Nhằm ph&aacute;t động phong tr&agrave;o t&igrave;m hiểu v&agrave; ứng dụng những lời Phật dạy v&agrave;o đời sống thường nhật, gi&uacute;p c&acirc;n bằng v&agrave; chuyển h&oacute;a th&acirc;n t&acirc;m, đem lại hạnh ph&uacute;c ch&acirc;n thật cho mỗi c&aacute; nh&acirc;n, gia đ&igrave;nh v&agrave; x&atilde; hội.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- Tiếp nối truyền thống tri &acirc;n v&agrave; b&aacute;o &acirc;n Phật, tưởng nhớ đến &acirc;n đức v&ocirc; lượng m&agrave; trong suốt cuộc đời hoằng ph&aacute;p, Ng&agrave;i đ&atilde; kh&ocirc;ng hề mệt mỏi để &acirc;n cần truyền trao cho tất cả ch&uacute;ng ta.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- G&oacute;p phần n&acirc;ng cao đời sống tinh thần, &ocirc;n lại v&agrave; suy gẫm những lời v&agrave;ng của Phật. V&agrave; cũng l&agrave; cơ hội để ch&uacute;ng ta chi&ecirc;m nghiệm, nh&igrave;n lại những h&agrave;nh động, lời n&oacute;i v&agrave; &yacute; nghĩ sai lầm trong qu&aacute; khứ, từ đ&oacute; điều chỉnh v&agrave; ph&aacute;t huy đời sống thiện l&agrave;nh trong tương lai.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">II. THỂ LỆ CUỘC THI</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">1. Giới thiệu về Kinh Ph&aacute;p C&uacute;</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Kinh Ph&aacute;p C&uacute;</span>&nbsp;hoặc&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Kinh Lời V&agrave;ng</span>&nbsp;hay c&ograve;n được gọi l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Lời Phật Dạy</span>,&nbsp;l&agrave; một trong 15 quyển kinh thuộc&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Tiểu bộ kinh</span>&nbsp;trong&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Kinh Tạng Pali</span>. &ETH;&acirc;y l&agrave; một quyển kinh Phật gi&aacute;o&nbsp;rất phổ th&ocirc;ng v&agrave; đ&atilde; được dịch ra nhiều thứ tiếng tr&ecirc;n thế giới. Nhiều t&aacute;c giả xem bộ kinh n&agrave;y như&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Th&aacute;nh thư của đạo Phật</span>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Kinh Ph&aacute;p C&uacute; l&agrave; một tập hợp những lời dạy ngắn gọn nhưng đầy &yacute; nghĩa của đức Phật Th&iacute;ch Ca&nbsp;trong ba trăm trường hợp gi&aacute;o h&oacute;a kh&aacute;c nhau, trong suốt 45 năm thuyết ph&aacute;p của Ng&agrave;i. Kinh Ph&aacute;p C&uacute; t&oacute;m th&acirc;u tinh hoa gi&aacute;o l&yacute; của đức&nbsp;Phật&nbsp;tập hợp lại từ nhiều quyển kinh kh&aacute;c. Những c&acirc;u danh ng&ocirc;n của đức&nbsp;Phật được ghi tại c&aacute;c ch&ugrave;a&nbsp;hay c&aacute;c s&aacute;ch b&aacute;o về danh ng&ocirc;n phần lớn được tr&iacute;ch ra từ Kinh Ph&aacute;p C&uacute;. Tại Việt Nam&nbsp;bản dịch th&agrave;nh thơ của H&ograve;a thượng Th&iacute;ch Minh Ch&acirc;u&nbsp;được sử dụng rất phổ biến.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Một trong những kinh điển nghi&ecirc;n cứu về Phật học của người học Phật, Kinh Ph&aacute;p C&uacute; được xem như l&agrave; một bản t&oacute;m tắt truy cập những lời dạy của Đức Phật để đưa con người tự đến kh&aacute;m ph&aacute; sự thật cho ch&iacute;nh m&igrave;nh. Ph&aacute;p C&uacute; tiếng Pāli gọi l&agrave; Dhammapada v&agrave; Phạn ngữ dharmapāda viết theo mẫu Devanāgarī.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Kinh Ph&aacute;p C&uacute;, trong tiếng Pali: Dhammapada l&agrave; do hai chữ gh&eacute;p lại, Dhamma l&agrave; Ph&aacute;p c&oacute; nghĩa l&agrave; sự thật, nguy&ecirc;n tắc, học thuyết Phật gi&aacute;o, ph&aacute;p luật, v&agrave; kỷ luật, Pada c&oacute; nghĩa l&agrave; con đường, c&aacute;ch thức, bước ch&acirc;n. Cuốn Kinh n&agrave;y gồm 26 Phẩm, 423 b&agrave;i kệ, l&agrave; cuốn thứ hai trong 15 cuốn thuộc Kinh Tiểu Bộ (Khuddaka-Nikaya) trong Kinh tạng Pali v&agrave; đ&atilde; được dịch ra nhiều thứ tiếng kh&aacute;c nhau tr&ecirc;n thế giới. Kh&ocirc;ng c&oacute; kinh n&agrave;o được dịch ra nhiều thứ tiếng như tập Dhammapada n&agrave;y. Hầu hết c&aacute;c ng&ocirc;n ngữ quan trọng tr&ecirc;n thế giới đều c&oacute; bản dịch của kinh. Tại c&aacute;c nước theo Phật gi&aacute;o Nam T&ocirc;ng, kinh n&agrave;y đ&atilde; được c&aacute;c Sa-di học thuộc l&ograve;ng. Kh&ocirc;ng những 423 b&agrave;i kệ trong kinh n&agrave;y t&oacute;m th&acirc;u tinh hoa gi&aacute;o l&yacute; đức Phật, m&agrave; nhiều b&agrave;i kệ đ&atilde; trở th&agrave;nh những lời dạy ch&iacute;nh đức Phật th&acirc;n thuyết. V&agrave; đọc những b&agrave;i kệ n&agrave;y, ch&uacute;ng ta cảm th&acirc;n như nghe được ch&iacute;nh lời Phật dạy hiện tiền từ hơn 2000 năm c&ograve;n vọng lại. Gi&aacute; trị bất hủ của tập Dhammapada ch&iacute;nh ở chỗ n&agrave;y vậy.&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">2. Nội dung thi:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Người tham gia phải học thuộc l&ograve;ng Kinh Ph&aacute;p C&uacute; (bao gồm 26 phẩm, 423 b&agrave;i kệ, bản dịch do HT Th&iacute;ch Minh Ch&acirc;u bi&ecirc;n soạn). Bản kinh sẽ được gửi qua mail cho người đăng k&yacute; dự thi. Đ&acirc;y l&agrave; t&ecirc;n c&aacute;c mục phẩm của Kinh Ph&aacute;p C&uacute;:</p>\r\n</div>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">&nbsp;</p>\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">\r\n<ul style=\"box-sizing: border-box; margin: 15px 0px 15px 30px; padding: 0px; border: 0px; line-height: 1.35;\">\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; list-style-position: outside; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">PHẨM 01-10</span></li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">I. Phẩm Song Yếu</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">II.Phẩm Kh&ocirc;ng Ph&oacute;ng Dật</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">III. Phẩm T&acirc;m</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">IV. Phẩm Hoa</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">V. Phẩm Ngu</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">VI. Phẩm Hiền Tr&iacute;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">VII. Phẩm A-La-H&aacute;n</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">VIII. Phẩm Ng&agrave;n</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">IX. Phẩm &Aacute;c</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">X. Phẩm H&igrave;nh Phạt&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<ul style=\"box-sizing: border-box; margin: 15px 0px 15px 30px; padding: 0px; border: 0px; line-height: 1.35;\">\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; list-style-position: outside; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">PHẨM 11-20</span></li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XI. Phẩm Gi&agrave;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XII. Phẩm Tự Ng&atilde;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XIII. Phẩm Thế Gian</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XIV. Phẩm Phật Đ&agrave;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XV. Phẩm An Lạc</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XVI. Phẩm Hỷ &Aacute;i</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XVII. Phẩm Phẫn Nộ</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XVIII. Phẩm Cấu Uế</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XIX. Phẩm Ph&aacute;p Trụ</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XX. Phẩm Đạo</p>\r\n<ul style=\"box-sizing: border-box; margin: 15px 0px 15px 30px; padding: 0px; border: 0px; line-height: 1.35;\">\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; list-style-position: outside; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">PHẨM 21-26</span></li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XXI. Phẩm Tạp Lục</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XXII. Phẩm Địa Ngục</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XXIII. Phẩm Voi</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XXIV. Phẩm Tham &Aacute;i</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XXV. Phẩm Tỷ Kheo</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">XXVI. Phẩm B&agrave;-La-M&ocirc;n.</p>\r\n</div>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">III. ĐỐI TƯỢNG THAM GIA</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Đối tượng tham gia cuộc thi: Chư Tăng, Phật tử v&agrave; tất cả mọi người, kh&ocirc;ng giới hạn số lượng. Ban tổ chức sẽ t&ugrave;y theo số lượng người tham gia m&agrave; c&oacute; thể sắp xếp th&agrave;nh nhiều đợt thi.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">IV. THỜI GIAN, ĐỊA ĐIỂM TỔ CHỨC THI</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">1. Thời gian tổ chức:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- Thời gian đăng k&yacute; dự thi: Từ ng&agrave;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">01/08/2019</span>&nbsp;đến hết ng&agrave;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">15/09/2019</span>&nbsp;(nhằm&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">01/07 đến 17/08&nbsp;Kỷ Hợi</span>). Khi đăng k&yacute; hoan hỷ gửi th&ocirc;ng tin c&aacute; nh&acirc;n&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">(họ t&ecirc;n, ng&agrave;y sinh, số CMND, nơi ở hiện tại v&agrave; số điện thoại)</span>&nbsp;đến địa chỉ&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">email:</span>&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; color: #075dc3; text-decoration-line: none; margin: 0px; padding: 0px; border: 0px; overflow-wrap: break-word;\" href=\"mailto:ngondenphapbao.hp@gmail.com\">ngondenphapbao.hp@gmail.com</a>. Người dự thi ch&iacute;nh thức được x&aacute;c nhận đ&atilde; đăng k&yacute; khi c&oacute; thư ph&uacute;c đ&aacute;p của Ban Tổ Chức th&ocirc;ng qua địa chỉ&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">email:&nbsp;</span><a style=\"box-sizing: border-box; background-color: transparent; color: #075dc3; text-decoration-line: none; margin: 0px; padding: 0px; border: 0px; overflow-wrap: break-word;\" href=\"mailto:ngondenphapbao.hp@gmail.com\">ngondenphapbao.hp@gmail.com</a>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- Thời gian thi ch&iacute;nh thức: Ng&agrave;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">06/10/2019</span>&nbsp;(nhằm&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">08/09</span>&nbsp;Kỷ Hợi).</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">-&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Lưu &yacute;:</span>&nbsp;Người dự thi phải mang theo điện thoại để thu &acirc;m b&agrave;i đọc thuộc l&ograve;ng Kinh Ph&aacute;p C&uacute;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">2. Địa điểm tổ chức thi:</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- Người dự thi sẽ tập trung về&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Giảng Đường Ch&iacute;nh Ch&ugrave;a Hoằng Ph&aacute;p</span>&nbsp;trước&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">7h</span>&nbsp;s&aacute;ng ng&agrave;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">06/10/2019</span>&nbsp;(nhằm&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">08/09</span>&nbsp;Kỷ Hợi).</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">- Địa chỉ:&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Ch&ugrave;a Hoằng Ph&aacute;p, số 188/8 Ấp T&acirc;n Thới 3, x&atilde; T&acirc;n Hiệp, Huyện H&oacute;c M&ocirc;n, Tp. Hồ Ch&iacute; Minh.</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">V. GIẢI THƯỞNG</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Ban Tổ chức sẽ căn cứ v&agrave;o số lượng v&agrave; chất lượng cuộc thi để trao giải thưởng cho c&aacute;c c&aacute; nh&acirc;n đạt giải. C&aacute;c c&aacute; nh&acirc;n đạt giải sẽ được Ban Tổ chức tặng&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Giấy Chứng Nhận Tham Dự Chương Tr&igrave;nh, những chiếc c&uacute;p lưu niệm v&agrave; những phần qu&agrave; Ph&aacute;p bảo</span>&nbsp;do ch&ugrave;a Hoằng Ph&aacute;p thực hiện.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">V&agrave; đặc biệt Ban Tổ Chức c&ograve;n tặng&nbsp;<em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">một chuyến tham quan du lịch miễn ph&iacute; một số thắng cảnh miền Bắc</span></em>&nbsp;cho c&aacute;c c&aacute; nh&acirc;n đạt giải. Cụ thể Đo&agrave;n sẽ tổ chức tham quan c&aacute;c địa điểm hấp dẫn như sau:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">1. Khu du lịch ch&ugrave;a Tam Ch&uacute;c</span>&nbsp;tọa lạc tr&ecirc;n mảnh đất Ba Sao, Kim Bảng, H&agrave; Nam được mệnh danh l&agrave; Hạ Long tr&ecirc;n cạn với một quần thể đẹp như c&otilde;i mộng. Nơi đ&acirc;y được Thủ tướng c&ocirc;ng nhận l&agrave; Khu du lịch quốc gia, vừa qua&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Ch&ugrave;a Tam Ch&uacute;c</span>&nbsp;được vinh dự l&agrave; nơi đăng cai tổ chức&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Đại lễ Vesak năm 2019</span>&nbsp;(Đại hội Phật gi&aacute;o thế giới) tổ chức v&agrave;o th&aacute;ng 5.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Quần thể&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">khu du lịch Tam Ch&uacute;c, Ba Sao</span>&nbsp;thực sự l&agrave; một điểm đến t&acirc;m linh hấp dẫn, bởi nơi đ&acirc;y l&agrave; sự kết hợp ho&agrave;n hảo giữa vẻ đẹp cổ k&iacute;nh của ng&ocirc;i ch&ugrave;a ngh&igrave;n năm tuổi với vẻ h&ugrave;ng vĩ của non nước bao la. Đặc biệt, kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; tiếng chim h&oacute;t l&iacute;u lo giữa n&uacute;i rừng rộng lớn l&agrave; điều m&agrave; bất k&igrave; ai cũng sẽ kh&ocirc;ng thể n&agrave;o qu&ecirc;n khi đặt ch&acirc;n đến mảnh đất n&agrave;y.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">2. Ch&ugrave;a B&aacute;i Đ&iacute;nh</span>&nbsp;l&agrave; một khu du lịch t&acirc;m linh&nbsp;lớn với nhiều kỷ lục ch&acirc;u &Aacute; v&agrave; Việt Nam được x&aacute;c lập như&nbsp;<em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a c&oacute; tượng Phật bằng đồng d&aacute;t v&agrave;ng lớn nhất ch&acirc;u &Aacute;, h&agrave;nh lang&nbsp;La H&aacute;n d&agrave;i nhất ch&acirc;u &Aacute;,&nbsp;c&oacute; tượng&nbsp;Di Lặc&nbsp;bằng&nbsp;đồng lớn nhất&nbsp;Đ&ocirc;ng Nam &Aacute;...</span></em>&nbsp;Năm 2010&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a B&aacute;i Đ&iacute;nh</span>&nbsp;l&agrave; nơi tổ chức Đại lễ cung nghinh&nbsp;x&aacute; lợi&nbsp;Phật đầu ti&ecirc;n từ Ấn Độ về&nbsp;Việt Nam.&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Đại lễ Phật đản&nbsp;Vesak 2014&nbsp;</span>do Việt Nam đăng cai đ&atilde; diễn ra tại ch&ugrave;a B&aacute;i Đ&iacute;nh v&agrave;o th&aacute;ng 3.&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Ch&ugrave;a B&aacute;i Đ&iacute;nh</span>&nbsp; nằm ở cửa ng&otilde; ph&iacute;a t&acirc;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">khu di t&iacute;ch&nbsp;cố đ&ocirc; Hoa Lư - Ninh B&igrave;nh</span>, ph&iacute;a bắc của<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">&nbsp;quần thể di sản thế giới Tr&agrave;ng An</span>. Ch&ugrave;a nằm tr&ecirc;n sườn n&uacute;i, giữa những thung lũng m&ecirc;nh m&ocirc;ng hồ v&agrave; n&uacute;i đ&aacute;. Ngo&agrave;i ra nơi đ&acirc;y c&ograve;n c&oacute;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">giếng ngọc cổ</span>&nbsp;lớn nhất Việt Nam v&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">đền thờ thần Cao Sơn</span>, l&agrave; một di t&iacute;ch trong&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Hoa Lư tứ trấn</span>&nbsp;được t&ocirc;n tạo từ thời&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">vua Đinh Ti&ecirc;n Ho&agrave;ng</span>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">3. Ch&ugrave;a Hương</span>&nbsp;hay&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Hương Sơn</span>&nbsp;l&agrave; cả một quần thể văn h&oacute;a - t&ocirc;n gi&aacute;o Việt Nam, gồm h&agrave;ng chục ng&ocirc;i ch&ugrave;a thờ Phật, v&agrave;i ng&ocirc;i đền thờ thần, c&aacute;c ng&ocirc;i đ&igrave;nh, thờ t&iacute;n ngưỡng n&ocirc;ng nghiệp. Trung t&acirc;m ch&ugrave;a Hương nằm ở x&atilde;&nbsp;Hương Sơn, huyện&nbsp;Mỹ Đức,&nbsp;H&agrave; Nội. Trung t&acirc;m của cụm&nbsp;đền&nbsp;ch&ugrave;a tại v&ugrave;ng n&agrave;y ch&iacute;nh l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a Hương</span>&nbsp;nằm trong&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">động Hương T&iacute;ch</span>&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a Trong</span>. Quần thể ch&ugrave;a Hương c&oacute; nhiều c&ocirc;ng tr&igrave;nh kiến tr&uacute;c rải r&aacute;c trong<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">&nbsp;thung lũng suối Yến</span>. Khu vực ch&iacute;nh l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a Ngo&agrave;i</span>, c&ograve;n gọi l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a Tr&ograve;</span>, t&ecirc;n chữ l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a Thi&ecirc;n Tr&ugrave;</span>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Lễ hội ch&ugrave;a Hương l&agrave; nơi hội tụ c&aacute;c sinh hoạt văn h&oacute;a d&acirc;n tộc độc đ&aacute;o như bơi thuyền, leo n&uacute;i v&agrave; c&aacute;c chiếu h&aacute;t ch&egrave;o, h&aacute;t văn &hellip; V&agrave;o những ng&agrave;y tổ chức lễ hội, ch&ugrave;a Hương tấp nập v&agrave;o ra h&agrave;ng trăm thuyền. N&eacute;t độc đ&aacute;o của hội ch&ugrave;a Hương l&agrave; th&uacute; vui ngồi thuyền v&atilde;ng cảnh lạc v&agrave;o non ti&ecirc;n c&otilde;i Phật.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">4. Tr&agrave;ng An</span>&nbsp;l&agrave; một khu du lịch sinh th&aacute;i nằm trong<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">&nbsp;Quần thể di sản thế giới Tr&agrave;ng An</span>&nbsp;thuộc tỉnh&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Ninh B&igrave;nh</span>. Nơi đ&acirc;y đ&atilde; được&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">UNESCO</span>&nbsp;c&ocirc;ng nhận l&agrave;&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">di sản thế giới k&eacute;p</span>&nbsp;từ năm 2014.&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Tr&agrave;ng An</span>&nbsp;với hệ thống d&atilde;y n&uacute;i đ&aacute;, s&ocirc;ng suối, rừng qua thời gian d&agrave;i biến đổi của tr&aacute;i đất, kh&iacute; hậu, đ&atilde; mang trong m&igrave;nh h&agrave;ng trăm thung lũng, hang động, hồ đầm. Hiện nay nơi đ&acirc;y c&ograve;n nhiều di t&iacute;ch lịch sử thời Đinh v&agrave; thời Trần.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Tr&agrave;ng An</span>&nbsp;l&agrave; một v&ugrave;ng non&nbsp;nước, m&acirc;y&nbsp;trời&nbsp;ho&agrave; quyện. Đ&aacute;y nước trong xanh soi b&oacute;ng những v&aacute;ch&nbsp;n&uacute;i&nbsp;đ&aacute;&nbsp;tr&ugrave;ng điệp. C&aacute;c d&atilde;y&nbsp;n&uacute;i,&nbsp;hồ&nbsp;nước&nbsp;v&agrave;&nbsp;hang&nbsp;động&nbsp;tạo th&agrave;nh một thế trận li&ecirc;n ho&agrave;n. Mỗi hồ l&agrave; một bức tranh thủy mặc kh&aacute;c nhau về thế n&uacute;i, d&aacute;ng n&uacute;i v&agrave; hồ nước. N&uacute;i giăng th&agrave;nh luỹ bao bọc quanh hồ nước v&agrave; ở giữa nổi l&ecirc;n khu đất với rừng c&acirc;y mọc th&agrave;nh đảo. Xa xa tr&ecirc;n triền n&uacute;i, dưới t&aacute;n c&acirc;y rừng l&uacute;p x&uacute;p l&agrave; những đ&agrave;n d&ecirc; n&uacute;i leo tr&egrave;o tr&ecirc;n c&aacute;c tảng đ&aacute; ch&ecirc;nh v&ecirc;nh.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">Trong&nbsp;quần thể danh thắng Tr&agrave;ng An,&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">trung t&acirc;m&nbsp;cố đ&ocirc; Hoa Lư</span>&nbsp;ở vị tr&iacute; ph&iacute;a bắc,&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">khu du lịch&nbsp;Tam Cốc - B&iacute;ch Động</span>&nbsp;ở vị tr&iacute; ph&iacute;a nam c&ograve;n&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">khu du lịch sinh th&aacute;i Tr&agrave;ng An</span>th&igrave; ở vị tr&iacute; trung t&acirc;m. 3 khu vực n&agrave;y được li&ecirc;n kết với nhau bằng khu rừng đặc dụng Hoa Lư tr&ecirc;n n&uacute;i đ&aacute; v&ocirc;i v&agrave; hệ thống s&ocirc;ng, hồ, đầm. Ngo&agrave;i ra, khu du lịch n&agrave;y c&ograve;n rất gần c&aacute;c địa điểm h&uacute;t kh&aacute;ch du lịch kh&aacute;c như Hang M&uacute;a, đầm V&acirc;n Long...</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">V&agrave; nếu thời gian cho ph&eacute;p, đo&agrave;n sẽ gh&eacute; thăm một số địa điểm tham quan kh&aacute;c như&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">ch&ugrave;a Trấn Quốc (H&agrave; Nội), ch&ugrave;a Di&ecirc;n Quang (Bắc Ninh), ch&ugrave;a T&acirc;y Kh&aacute;nh (Th&aacute;i B&igrave;nh),&hellip;v.v.&nbsp;</span>Dự kiến thời gian đi sẽ diễn ra trong 5 ng&agrave;y, từ ng&agrave;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">23/10/2019</span>&nbsp;đến&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">27/10/2019</span>&nbsp;(nhằm ng&agrave;y&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">25/09</span>&nbsp;đến&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">29/09</span>&nbsp;Kỷ Hợi).&nbsp;<span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Ban Tổ Chức sẽ t&agrave;i trợ to&agrave;n bộ chuyến đi cho c&aacute;c c&aacute; nh&acirc;n đạt giải bao gồm: v&eacute; m&aacute;y bay, xe dịch vụ di chuyển đưa đ&oacute;n đến c&aacute;c địa điểm tham quan, v&eacute; tham quan, ăn uống (d&ugrave;ng thực phẩm chay) v&agrave; ngủ nghỉ tại ch&ugrave;a.</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Lưu &yacute;:</span>&nbsp;Mọi chi tiết thắc mắc về cuộc thi, xin vui l&ograve;ng li&ecirc;n hệ số điện thoại:&nbsp;<em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">028(37).130.002</span></em>&nbsp;hoặc&nbsp;<em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">0977796952 (Thầy T&acirc;m Mạnh)</span></em></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: justify;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">TM.BAN TỔ CHỨC CUỘC THI</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">TRỤ TR&Igrave; CH&Ugrave;A HOẰNG PH&Aacute;P</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\">&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">Thượng tọa</em>&nbsp;Th&iacute;ch Ch&acirc;n T&iacute;nh</span></p>\r\n</div>\r\n</div>','2019-08-22 03:03:19','2019-08-22 03:03:19'),(2,'THƯ VẬN ĐỘNG HÙN PHƯỚC XÂY DỰNG TÒA NHÀ ĐA NĂNG','th-van-dong-hun-ph-oc-xay-dung-toa-nha-da-nang','<h1 class=\"tt-ss tt-md\" style=\"box-sizing: border-box; margin: 0px 0px 8px; font-size: 22px; font-family: Roboto, Helvetica, Arial, sans-serif; font-weight: 500; line-height: 1.2; color: #194d7b; padding: 0px; border: 0px; position: relative; text-transform: uppercase; text-align: center;\">THƯ VẬN ĐỘNG H&Ugrave;N PHƯỚC X&Acirc;Y DỰNG T&Ograve;A NH&Agrave; ĐA NĂNG</h1>\r\n<div class=\"content-detail-post\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-size: 12pt; color: #222222; font-family: Roboto, Helvetica, Arial, sans-serif;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; color: #0000cd; font-size: x-large;\">THƯ VẬN ĐỘNG</span></span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: center;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">K&iacute;nh bạch chư T&ocirc;n đức Tăng Ni.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">K&iacute;nh thưa qu&yacute; Phật tử gần xa,</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Hoằng ph&aacute;p lợi sinh l&agrave; tr&aacute;ch nhiệm v&agrave; bổn phận thi&ecirc;ng li&ecirc;ng cao cả của những người con Phật. Muốn mọi người đến ch&ugrave;a t&igrave;m hiểu Phật ph&aacute;p v&agrave; ph&aacute;t t&acirc;m tu tập, ch&uacute;ng ta phải c&oacute; nơi ăn chốn ở v&agrave; cung cấp những điều kiện vật chất cần thiết cho họ. V&igrave; vậy, x&acirc;y dựng ng&ocirc;i Tam bảo cũng như thiết lập đạo tr&agrave;ng l&agrave; việc l&agrave;m mang đến nhiều lợi lạc v&agrave; phước b&aacute;u cho mọi người.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Tr&ecirc;n tinh thần v&igrave; lợi &iacute;ch ch&uacute;ng sinh, bao năm nay, Thượng tọa Trụ tr&igrave; c&ugrave;ng chư Tăng ch&ugrave;a Hoằng Ph&aacute;p lu&ocirc;n cố gắng kiến lập cơ sở vật chất v&agrave; tổ chức c&aacute;c chương tr&igrave;nh tu học kh&aacute;c nhau sao cho ph&ugrave; hợp với từng căn cơ, tuổi t&aacute;c nhằm thu h&uacute;t nhiều th&agrave;nh phần trong x&atilde; hội đến ch&ugrave;a tu tập. C&oacute; những kh&oacute;a tu số lượng Phật tử về ch&ugrave;a tu tập rất đ&ocirc;ng, nh&agrave; ch&ugrave;a kh&ocirc;ng đ&aacute;p ứng đủ nơi ăn chỗ ở. Ch&iacute;nh v&igrave; vậy, việc x&acirc;y dựng một t&ograve;a nh&agrave; đa năng ki&ecirc;n cố để đ&aacute;p ứng nhu cầu cho c&aacute;c kh&oacute;a tu l&agrave; điều rất cần thiết. T&ograve;a nh&agrave; bốn tầng được thiết kế bao gồm: ph&ograve;ng nghỉ, trai đường, giảng đường lớn,&hellip;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Đầu năm 2018, việc x&acirc;y dựng đ&atilde; ch&iacute;nh thức được khởi c&ocirc;ng. Nhằm gi&uacute;p c&ocirc;ng tr&igrave;nh sớm được ho&agrave;n thiện v&agrave; đi v&agrave;o hoạt động, tạo điều kiện thuận lợi cho việc phục vụ c&aacute;c chương tr&igrave;nh Phật sự, tu học của Tăng ch&uacute;ng v&agrave; Phật tử gần xa, ch&uacute;ng t&ocirc;i tha thiết k&ecirc;u gọi to&agrave;n thể qu&yacute; Phật tử, c&aacute;c vị Cấp C&ocirc; Độc hoan hỷ ph&aacute;t t&acirc;m h&ugrave;n phước, c&uacute;ng dường tịnh t&acirc;m, tịnh t&agrave;i, tịnh vật, tịnh lực,&hellip; để gi&uacute;p qu&yacute; Phật tử c&oacute; nơi tu tập tốt hơn.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">H&ocirc;m nay, ch&uacute;ng t&ocirc;i xin chia sẻ th&ocirc;ng tin để qu&yacute; Phật tử biết v&agrave; ph&aacute;t t&acirc;m chung tay đ&oacute;ng g&oacute;p. Ch&acirc;n th&agrave;nh cảm niệm c&ocirc;ng đức v&agrave; t&aacute;n dương tấm l&ograve;ng v&agrave;ng của qu&yacute; Phật tử. K&iacute;nh ch&uacute;c qu&yacute; vị c&ugrave;ng to&agrave;n thể gia quyến th&acirc;n t&acirc;m an lạc, sở nguyện t&ugrave;y t&acirc;m, c&aacute;t tường như &yacute;.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Mọi sự đ&oacute;ng g&oacute;p xin li&ecirc;n hệ trực tiếp:</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Tại Văn ph&ograve;ng ch&ugrave;a Hoằng Ph&aacute;p</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">X&atilde; T&acirc;n Hiệp, Huyện H&oacute;c M&ocirc;n, TP HCM.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Điện thoại: (028) 37130002 - (028) 37133827</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">&nbsp;</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\">Hoặc chuyển khoản v&agrave;o t&agrave;i khoản đại diện duy nhất của ch&ugrave;a Hoằng Ph&aacute;p:</span></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">T&ecirc;n t&agrave;i khoản: Nguyễn Sỹ Cường (Thượng tọa Th&iacute;ch Ch&acirc;n T&iacute;nh)</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Số t&agrave;i khoản: 0071000651615</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Ng&acirc;n h&agrave;ng Vietcombank chi nh&aacute;nh TPHCM, số 29 Bến Chương Dương, Q.1, TP Hồ Ch&iacute; Minh (Ng&acirc;n h&agrave;ng TMCP Ngoại thương Việt Nam)</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\">Nội dung gửi v&agrave;o t&agrave;i khoản, xin qu&yacute; vị hoan hỷ ghi r&otilde;: &ldquo;H&ugrave;n Phước X&acirc;y Dựng T&ograve;a Nh&agrave; Đa Năng&rdquo;, để được sử dụng đ&uacute;ng mục đ&iacute;ch v&agrave; cập nhật th&ocirc;ng tin tr&ecirc;n Website.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold; margin: 0px; padding: 0px; border: 0px;\"><u style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">Lưu &yacute;:</u></span>&nbsp;Quỹ kh&ocirc;ng cử người đi quy&ecirc;n g&oacute;p, nếu qu&yacute; Phật tử ph&aacute;t t&acirc;m ủng hộ c&oacute; thể đến trực tiếp Văn ph&ograve;ng ch&ugrave;a Hoằng Ph&aacute;p, x&atilde; T&acirc;n Hiệp, huyện H&oacute;c M&ocirc;n, TP Hồ Ch&iacute; Minh hoặc th&ocirc;ng qua số t&agrave;i khoản của Quỹ.</p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px;\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px;\">Nam m&ocirc; C&ocirc;ng Đức L&acirc;m Bồ T&aacute;t Ma Ha T&aacute;t!</em></p>\r\n<p class=\"MsoNormal\" style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; border: 0px; text-align: right;\">Ch&ugrave;a Hoằng Ph&aacute;p, ng&agrave;y 01 th&aacute;ng 01 năm 2018</p>\r\n</div>\r\n</div>','2019-08-22 03:04:02','2019-08-22 03:04:02');
/*!40000 ALTER TABLE `notifications` ENABLE KEYS */;
UNLOCK TABLES;

